import { useState } from 'react';
import './App.css';

function App() {

  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [country, setCountry] = useState('');
  const [position, setPosition] = useState('');
  const [wage, setWage] = useState('');

  const displayInfo = () => {
    console.log(name + age + country + position + wage);
  }
  return (
    <div className="App">
      <div className='information'>
        <label>Name</label>
        <input type="text" onChange={(event) => { setName(event.target.value) }} />
        <label>Age</label>
        <input type="number" onChange={(event) => { setAge(event.target.age) }} />
        <label>Position</label>
        <input type="text" onChange={(event) => { setPosition(event.target.position) }} />
        <label>Country</label>
        <input type="text" onChange={(event) => { setCountry(event.target.country) }} />
        <label>Wage</label>
        <input type="text" onChange={(event) => { setWage(event.target.wage) }} />
        <button onClick={displayInfo}>Add Employee</button>
      </div>
    </div>
  );
}

export default App;
